mapper.select:
SELECT p.*, b.name AS brandname, co.name AS categoryonename, ct.name AS categorytwoname,
m.name AS merchantname
FROM product AS p
LEFT JOIN merchant AS m ON m.id = p.merchant_id
LEFT JOIN category_one AS co ON co.id = p.categoryone_id
LEFT JOIN category_two AS ct ON ct.id = p.categorytwo_id
LEFT JOIN brand AS b ON b.id = p.brand_id


        AND p.brand_id = #{brandid}


        AND p.categoryone_id = #{categoryoneid}


        AND p.categorytwo_id = #{categorytwoid}


        AND p.name LIKE CONCAT('%', #{name}, '%')


        AND p.shelves_status = #{shelvesstatus}


        AND p.merchant_id = #{merchantid}


        AND p.merchant_type = #{merchanttype}



    LIMIT #{startPage,jdbcType=INTEGER}, #{pageSize,jdbcType=INTEGER}

SELECT COUNT(p.*)
FROM product AS p
LEFT JOIN merchant AS m ON m.id = p.merchant_id
LEFT JOIN category_one AS co ON co.id = p.categoryone_id
LEFT JOIN category_two AS ct ON ct.id = p.categorytwo_id
LEFT JOIN brand AS b ON b.id = p.brand_id


        AND p.brand_id = #{brandid}


        AND p.categoryone_id = #{categoryoneid}


        AND p.categorytwo_id = #{categorytwoid}


        AND p.name LIKE CONCAT('%', #{name}, '%')


        AND p.shelves_status = #{shelvesstatus}


        AND p.merchant_id = #{merchantid}


        AND p.merchant_type = #{merchanttype}

SELECT p.*, b.name AS brandname, co.name AS categoryonename, ct.name AS categorytwoname, m.name AS merchantname, pr.time AS applytime FROM product AS p LEFT JOIN merchant AS m ON m.id = p.merchant_id LEFT JOIN category_one AS co ON co.id = p.categoryone_id LEFT JOIN category_two AS ct ON ct.id = p.categorytwo_id LEFT JOIN brand AS b ON b.id = p.brand_id LEFT JOIN product_review AS pr ON pr.product_id = p.id WHERE p.merchant_id IS NOT NULL
AND p.name LIKE CONCAT('%', #{name}, '%')
LIMIT #{startPage,jdbcType=INTEGER}, #{pageSize,jdbcType=INTEGER}
